#!/bin/bash

RESET="\033[0m"
BLACK="\033[30m"
RED="\033[31m"
GREEN="\033[32m"
YELLOW="\033[33m"
BLUE="\033[34m"
MAGENTA="\033[35m"
CYAN="\033[36m"
WHITE="\033[37m"

BOLDBLACK="\033[1m\033[30m"
BOLDRED="\033[1m\033[31m"
BOLDGREEN="\033[1m\033[32m"
BOLDYELLOW="\033[1m\033[33m"
BOLDBLUE="\033[1m\033[34m"
BOLDMAGENTA="\033[1m\033[35m"
BOLDCYAN="\033[1m\033[36m"
BOLDWHITE="\033[1m\033[37m"

make ../ > /dev/null
cp ../minishell .
chmod 755 minishell

function test()
{
	TEST1=$(./minishell $@)
	RES_T1=$?
	TEST2=$(echo $@ | bash 2>&-)
	RES_T2=$?
	printf "$@ :"
	if [ "$TEST1" == "$TEST2" ] && [ "$RES_T1" == "$RES_T2" ]; then
		printf " $BOLDGREEN%s$RESET\n" "âœ“"
	else
		printf " $BOLDRED%s$RESET\n" "âœ—"
	fi
	if [ "$TEST1" != "$TEST2" ]; then
		echo
		printf "Your output : $BOLDRED$TEST1$RESET"
		echo
		printf "Expected : $BOLDGREEN$TEST2$RESET"
		echo
	fi
}

test 'echo bonjour'
# ECHO TESTS
test 'echo test tout'
test 'echo test      tout'
test 'echo -n test tout'
test 'echo -n -n -n test tout'


